css:
/*titulo*/
#s-PELUCIAS {
grid-template-columns: repeat(auto-fit,minmax(50%,1fr)) !important;
}
/*ORDEM TIMELINE PAGINA PEDIDO DO CLIENTE*/
.order-info__timeline {
display: flex !important;
flex-direction: column-reverse;
}
/*ordem apresentacao formas de pagamento ao cliente*/
.payment__gateway--1238[data-label="Cartão de crédito"] {order: -4}
.payment__gateway--1238[data-label="Débito Online"] {order: -1}
.payment__gateway--108091[data-label="PIX - seu pedido será confirmado na hora"] {order: -3}
.payment__gateway--1251[data-label="Boleto bancário"] {order: -2}

/*imagens de pagamento centralizadas*/
.payment__gateway .payment__head + .payment__stamp {
  display: flex;
}

/*tarja de informação de pagamento maior*/
.payment__badges .badge {
  font-size: 90%
}

/*codigo QR PIX após finalizar pedido*/
.payment__gateway--108091[data-label="PIX - seu pedido será confirmado na hora"] + .payment__method .payment__text {
  display: none
}
